function updateTaskStatus(
  data: ProjectData,
  completedTokens: Set<string>
): ProjectData {
  const updated = { ...data };

  for (const milestone of updated.milestones) {
    for (const task of milestone.tasks) {
      if (completedTokens.has(task.id)) {
        task.status = "done";
      }
    }
  }

  return updated;
}

